(()=>{document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll(".highlight").forEach(function(t){let e=document.createElement("button");e.className="copy-button",e.textContent="Copy",e.setAttribute("aria-label","Copy code to clipboard"),t.appendChild(e),e.addEventListener("click",async function(){let n=t.querySelector("pre code")||t.querySelector("code"),c="";if(n){let o=n.cloneNode(!0);o.querySelectorAll('span[style*="user-select:none"]').forEach(r=>r.remove()),c=o.textContent||""}try{await navigator.clipboard.writeText(c),e.textContent="Copied!",e.classList.add("copied"),setTimeout(function(){e.textContent="Copy",e.classList.remove("copied")},2e3)}catch(o){console.error("Failed to copy: ",o),e.textContent="Failed",setTimeout(function(){e.textContent="Copy"},2e3)}})})});window.resizeSketch=function(t){try{let e=t?.contentWindow?.document?.body?.querySelector("#defaultCanvas0");if(!e)return;t.style.height=e.style.height,t.style.width=e.style.width}catch{}};})();
